#include <iostream>#include <fstream>#include <vector>using namespace std;bool checkNumber(char el){    if (int(el) >= int('0') && int(el) <= int('9'))    {        return true;    }    return false;}string readFile(ifstream & fin){    string str;    string text = "";    while(!fin.eof())    {        getline(fin, str);        text += str;    }    return text;}void fileOpen(ifstream & fin){    string fileName;    cout << "Enter name file: ";    getline(cin, fileName);    if(fileName == "in")    {        fileName = "input.txt";    }    fin.open(fileName);    while(!fin.is_open())    {        cout << "Error: Please enter correct name file" << endl << "Enter name file: ";        getline(cin, fileName);        if(fileName == "in")        {            fileName = "input.txt";        }        fin.open(fileName.c_str());    }}void research(string text, vector<int> &result){    string number = "";    int check = 0;    for(int i = 0; i < text.length(); i++)    {        if(checkNumber(text[i]))        {            number += text[i];            if(!checkNumber(text[i + 1]))            {                for(int i = 1, max = int(number[0]) - int('0'); i < number.length(); i++)                {                    if(max == int(number[i]) - int('0') + 1)                    {                        check++;                        max = int(number[i]) - int('1') + 1;                    }                }                if(check + 1 == number.length())                {                    result.push_back(atoi(number.c_str()));                }                number = "";                check = 0;            }        }    }}void output(vector<int> & result, string text){    cout << "Original: " << text;    cout << "\nResult: ";    for(int i = 0; i < result.size(); i++)    {        cout << result[i] << ' ';    }    cout << '\n';}string repeat(string rep){    while(!(rep == "Y" || rep == "y" || rep == "N" || rep == "n"))    {        cout << "Error: incorrect data\nRepeat program?(Y/N): ";        getline(cin, rep);    }    return rep;}string deleteR(string text){    for(int i  = 0; i < text.length(); i++)    {        if(text[i] == '\r')        {            text[i] = '\n';        }    }    return text;}int main(){    ifstream fin;    string text, rep;    vector<int> result;    do {        fileOpen(fin);        text = readFile(fin);        text = deleteR(text);        research(text, result);        output(result, text);        fin.close();        cout << "Repeat program?(Y/N): ";        getline(cin, rep);        rep = repeat(rep);        result.clear();    } while (rep == "Y" || rep == "y");}